    // pancakeswapRoutes[wbtc][farm] = [wbtc, weth, usdc, farm];
    // pancakeswapRoutes[renBTC][farm] = [renBTC, weth, usdc, farm];
    // pancakeswapRoutes[sushi][farm] = [sushi, weth, usdc, farm];
    // pancakeswapRoutes[dego][farm] = [dego, weth, usdc, farm];
